INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1338296, 'Fluvoxamine', 'Benzatropine', 'Moderate', 'Delirium has been observed during the coadministration of benztropine and selective serotonin reuptake inhibitors (SSRIs). The mechanism of this potential interaction, if any, has not been determined. There have only been isolated case reports, and concomitant neuroleptic use was common.', 'DDInter', 1767317475, 'Until further data are available, it may be appropriate to monitor patients for evidence of delirium or other unusual psychiatric reactions when benztropine is given with SSRIs, especially if neuroleptic therapy is also administered.', 'Others', 'Until further data are available, it may be appropriate to monitor patients for evidence of delirium or other unusual psychiatric reactions when benztropine is given with SSRIs, especially if neuroleptic therapy is also administered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
