        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338395, 'Fluvoxamine', 'Methadone', 'Moderate', 'Coadministration with fluvoxamine may increase the serum concentrations of methadone. The mechanism is fluvoxamine inhibition of methadone metabolism via CYP450 2C9 and 3A4.', NULL, 'Caution is advised if methadone must be used in combination with fluvoxamine.', NULL, 'Caution is advised if methadone must be used in combination with fluvoxamine. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of fluvoxamine in patients who are stabilized on their methadone regimen. Patients should be advised to report excessive drowsiness, nausea, or asthenia to their doctor, and to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
