        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338509, 'Fosamprenavir', 'Dasatinib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of dasatinib during and for 2 weeks after treatment with itraconazole. Close monitoring for toxicity (e.g., myelosuppression, bleeding complications, fluid retention, excessive slowing of heart rate or other conduction disturbances) and a reduction of dasatinib dosage to a range of 20 to 40 mg daily should be considered if there are no alternatives and concomitant use with these agents is necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
