        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338511, 'Fosamprenavir', 'Deflazacort', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of deflazacort and CYP450 3A inhibitors unless the benefit outweighs the risk, in which case patients should be monitored for systemic corticosteroid side effects.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
