    ) VALUES (1338552, 'Fosaprepitant', 'Avanafil', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of avanafil, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
