        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338630, 'Foscarnet', 'Amifampridine', 'Moderate', 'The use of amifampridine is associated with a dose-related risk of seizures, and the risk may be further increased when coadministered with other drugs that can reduce the seizure threshold such as antidepressants, antimalarials, neuroleptics, phenothiazines, and quinolones.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
