        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338632, 'Foscarnet', 'Amikacin', 'Major', 'The potential nephrotoxicity of foscarnet and aminoglycosides may be additive. Concomitant use of these drugs could also lead to additive hypokalemia, hypomagnesemia, and hypocalcemia, which may increase the foscarnet-related risks of QT prolongation and seizures.', NULL, 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks.', NULL, 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks. Electrolyte levels and renal function of patients receiving these medications concomitantly should be monitored carefully, and dosages adjusted or discontinued if necessary.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
