    ) VALUES (1338685, 'Foscarnet', 'Dofetilide', 'Major', 'Like other class III antiarrhythmic agents, dofetilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
