INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24568, 'Amphotericin B (lipid complex)', 'Pentamidine', 'Moderate', 'The possible nephrotoxic side effects of pentamidine and amphotericin may be additive.', NULL, 'Serum creatinine and BUN should be monitored closely during therapy with these two agents.', NULL, 'Serum creatinine and BUN should be monitored closely during therapy with these two agents.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', 'Miltefosine, Fexinidazole, Nifurtimox, Benznidazole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24587/', '[1] "Product Information. Abelcet (amphotericin B)." Liposome Company Inc, The, Princeton, NJ.[2] Antoniskis D, Larsen RA "Acute, rapidly progressive renal failure with simultaneous use of amphotericin B and pentamidine." Antimicrob Agents Chemother 34 (1990): 470-2', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24569, 'Amphotericin B (lipid complex)', 'Pimozide', 'Major', 'Pimozide can cause dose-related prolongation of the QT interval. While clinical data are lacking, the coadministration of pimozide and agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', NULL, 'Caution is advised when pimozide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when pimozide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with pimozide.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Clindamycin, Ciclopirox, More', 'Methotrimeprazine, Quetiapine, Asenapine, Thiothixene, Pimavanserin, Lumateperone, Clozapine, Olanzapine, Aripiprazole, Loxapine, Molindone, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24588/', '[1] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[2] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[3] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24570, 'Amphotericin B (lipid complex)', 'Pipecuronium', 'Moderate', 'Amphotericin commonly causes renal potassium and magnesium wasting. As a result, serum potassium is decreased, and the pharmacologic effects of some neuromuscular blocking agents are enhanced.', NULL, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', NULL, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', 'Excretion', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Trichlormethiazide, Ciclopirox, More', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24589/', '[1] Cushard WG, Kohanim M, Lantis LR "Blastomycosis of bone." J Bone Joint Surg Am 51A (1969): 704-12[2] "Product Information. Amphotec (amphotericin B cholesteryl sulfate)." Sequus Pharmaceuticals Inc, Menlo Park, CA.[3] Miller R, Bates J "Amphotericin B toxicity." Ann Intern Med 71 (1969): 1089-95[4] "Product Information. Abelcet (amphotericin B)." Liposome Company Inc, The, Princeton, NJ.', 1767369485);
