INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24575, 'Amphotericin B (lipid complex)', 'Promazine', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Methotrimeprazine, Quetiapine, Asenapine, Thiothixene, Pimavanserin, Lumateperone, Clozapine, Olanzapine, Aripiprazole, Loxapine, Molindone, More', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Trichlormethiazide, Ciclopirox, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24594/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24576, 'Amphotericin B (lipid complex)', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24595/', '[1] "Product Information. Xofigo (radium Ra 223 dichloride)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
