INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24606, 'Ampicillin', 'Atenolol', 'Moderate', 'INTERVAL: The oral coadministration of ampicillin and atenolol may result in decreased plasma concentrations of the latter, possibly due to impaired gastrointestinal absorption in the presence of ampicillin.', NULL, 'These results suggest that the interaction may be attenuated by dividing the ampicillin dosage into smaller, more frequent doses and/or staggering the times of administration of the two drugs.', NULL, 'These results suggest that the interaction may be attenuated by dividing the ampicillin dosage into smaller, more frequent doses and/or staggering the times of administration of the two drugs.', 'Absorption', 'Pindolol, Labetalol, Metoprolol, Penbutolol, Acebutolol, Labetalol, Nadolol, Propranolol, Metoprolol, Carvedilol, Esmolol, More', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, Nafcillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24625/', '[1] Schafer-Korting M, Kirch W, Axthelm T, Kohler H, Mutschler E "Atenolol interaction with aspirin, allopurinol, and ampicillin." Clin Pharmacol Ther 33 (1983): 283-8[2] McLean AJ, Tonkin A, McCarthy P, Harrison P "Dose-dependence of atenolol-ampicillin interaction." Br J Clin Pharmacol 18 (1984): 969-71[3] McCarthy CG, Finland M "Absorption and excretion of four penicillins." N Engl J Med 263 (1960): 315-26[4] Cronk GA, Wheatley WB, Fellers GF, Albright H "The relationship of food intake to the absorption of potassium alpha-phenoxyethyl penicillin and potassium phenoxymethyl penicillin from the gastrointestinal tract."