INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24607, 'Ampicillin', 'Azithromycin', 'Minor', 'Although some in vitro data indicate synergism between macrolide antibiotics and penicillins, other in vitro data indicate antagonism. When these drugs are given together, neither has predictable therapeutic efficacy. Data are available for erythromycin, although theoretically this interaction could occur with any macrolide. Except for monitoring of the effectiveness of antibiotic therapy, no special precautions appear to be necessary.', NULL, '-', NULL, '-', 'Others', 'Tinidazole, Norfloxacin, Cefepime, Cefuroxime, Secnidazole, Cefixime, Levofloxacin, Gatifloxacin, Ganciclovir, Rifamycin, Norfloxacin, More', 'Tazobactam, Sulbactam, Rifamycin, Kanamycin, Oxytetracycline, Netilmicin, Polymyxin B, Sulfamethizole, Trifluridine, Sulfisoxazole, Azithromycin, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24626/', '[1] Strom J "Penicillin and erythromycin singly and in combination in scarlatina therapy and the interference between them." Antibiot Chemother 11 (1961): 694-7[2] Cohn JR, Jungkind DL, Baker JS "In vitro antagonism by erythromycin of the bactericidal action of antimicrobial agents against common respiratory pathogens." Antimicrob Agents Chemother 18 (1980): 872-6[3] Penn RL, Ward TT, Steigbigel RT "Effects of erythromycin in combination with penicillin, ampicillin, or gentamicin on the growth of listeria monocytogenes." Antimicrob Agents Chemother 22 (1982): 289-94', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24608, 'Ampicillin', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', 'Sulbactam, Oxacillin, Piperacillin, Meticillin, Ticarcillin, Mezlocillin, Tazobactam, Nafcillin, Benzylpenicillin, Procaine benzylpenicillin, Ganciclovir, More', 'Budesonide, Cromoglicic acid, Olsalazine, Sulfasalazine, Betamethasone, Beclomethasone dipropionate, Mesalazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24627/', '[1] "Product Information. Colazal (balsalazide)" Salix Pharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24609, 'Ampicillin', 'Bifidobacterium longum infantis', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Sulbactam, Piperacillin, Meticillin, Carbenicillin, Ticarcillin, Mezlocillin, Tazobactam, Nafcillin, Benzylpenicillin, Procaine benzylpenicillin, Ganciclovir, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24628/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24610, 'Ampicillin', 'Chloramphenicol', 'Moderate', 'Chloramphenicol may inhibit the bactericidal effect of penicillins when used to treat bacterial meningitis and staphylococcal endocarditis. Chloramphenicol acts by inhibiting cellular protein synthesis and can change actively growing bacteria into static ones. Since penicillins work by inhibiting cell wall synthesis, their effectiveness may be reduced. In addition, neurologic toxicity may be enhanced.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis. If the combination is necessary, some experts recommend administering the penicillin several hours before the chloramphenicol. Patients should be closely monitored for altered antimicrobial efficacy.', 'Antagonism', 'Sulbactam, Dicloxacillin, Bacampicillin, Phenoxymethylpenicillin, Tazobactam, Tetracycline, Gatifloxacin, Norfloxacin, Rifamycin, Kanamycin, Oxytetracycline, More', 'Mupirocin, Rifamycin, Bacitracin, Rifaximin, Neomycin, Rifamycin, Miconazole, Polymyxin B, Chlorhexidine, Neomycin, Vitamin A, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24629/', '[1] Shalit I, Marks MI "Chloramphenicol in the 1980s." Drugs 28 (1984): 281-91[2] Wallace JF, Smith RH, Garcia M, Petersdorf RG "Studies on the pathogenesis of meningitis. VI: antagonism between penicillin and chloramphenicol in experimental pneumococcal meningitis." J Lab Clin Med 70 (1967): 408-18[3] Jawetz E "The use of combinations of antimicrobial drugs." Annu Rev Pharmacol 8 (1968): 151[4] Garrod L "Causes of failure in antibiotic treatment." Br Med J 4 (1972): 441', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24611, 'Ampicillin', 'Chloroquine', 'Moderate', 'INTERVAL: Concomitant administration of chloroquine may delay as well as decrease the gastrointestinal absorption of ampicillin. The exact mechanism of interaction is unknown, but may involve slowing of gastric emptying and enhancement of gut motility induced by chloroquine.', NULL, 'To avoid potentially subtherapeutic plasma concentrations of ampicillin during coadministration with chloroquine, the drugs should be administered at least 2 hours apart.', NULL, 'To avoid potentially subtherapeutic plasma concentrations of ampicillin during coadministration with chloroquine, the drugs should be administered at least 2 hours apart.', 'Absorption', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Cloxacillin, Nafcillin, More', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24630/', '[1] Ali HM "Reduced ampicillin bioavailability following oral coadministration with chloroquine." J Antimicrob Chemother 15 (1985): 781-4[2] "Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] McCarthy CG, Finland M "Absorption and excretion of four penicillins." N Engl J Med 263 (1960): 315-26[5] Cronk GA, Wheatley WB, Fellers GF, Albright H "The relationship of food intake to the absorption of potassium alpha-phenoxyethyl penicillin and potassium phenoxymethyl