INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24825, 'Anagrelide', 'Milrinone', 'Moderate', 'Anagrelide is an inhibitor of cyclic AMP phosphodiesterase (PDE) III and may potentiate the pharmacologic effects of drugs with similar properties such as inotropes milrinone, enoximone, inamrinone, olprinone, and cilostazol. Anagrelide alone has been associated with cardiovascular side effects such as vasodilation, palpitations, tachycardia, congestive heart failure, myocardial infarction, cardiomyopathy, cardiomegaly, complete heart block, and atrial fibrillation.', NULL, 'Caution is advised if anagrelide is coadministered with other inotropes or inhibitors.', NULL, 'Caution is advised if anagrelide is coadministered with other inotropes or inhibitors. The risk of potentially additive cardiovascular adverse effects should be considered.', 'Synergism', 'Dobutamine, Norepinephrine, Midodrine, Droxidopa, Angiotensin II, Ephedrine, Arbutamine, Phenylephrine, Fenoldopam, Metaraminol, Epinephrine, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24844/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 1767369485);
