INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24946, 'Anagrelide', 'Ticlopidine', 'Moderate', 'Theoretically, coadministration with drugs that are inhibitors of CYP450 1A2 may increase the plasma concentrations of anagrelide, which is a substrate of the isoenzyme.', NULL, 'Clinicians should recognize the potential for interaction with drugs that inhibit CYP450 1A2 and monitor for evidence of anagrelide toxicities during coadministration.', NULL, 'Clinicians should recognize the potential for interaction with drugs that inhibit CYP450 1A2 and monitor for evidence of anagrelide toxicities during coadministration. Since platelet reduction from anagrelide therapy is dose-related, thrombocytopenia may occur due to reduced clearance of anagrelide and may be a sign of overdosage. Patients should also be monitored for cardiovascular adverse effects such as tachycardia, palpitations, hypotension, edema, and congestive heart failure.', 'Metabolism', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24965/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24947, 'Anagrelide', 'Tinzaparin', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy. If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended. Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, More', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24966/', '[1] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Arixtra (fondaparinux)." Organon, West Orange, NJ.[6] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[7] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395', 1767369485);
