INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24949, 'Anagrelide', 'Tirofiban', 'Major', 'The combination of glycoprotein IIb/IIIa inhibitors and anagrelide can theoretically significantly increase the risk of bleeding. GP IIb/IIIa inhibitors inhibit platelet aggregation by blocking the fibrinogen, von Willebrand factor, and other adhesive ligands to platelets. The mechanism whereby anagrelide decreases platelet concentrations is not known. The combination of these drugs, therefore, could decrease platelet number and function.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', NULL, 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24968/', '[1] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (24950, 'Anagrelide', 'Tizanidine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may significantly increase the plasma concentrations and pharmacologic effects of tizanidine, which is a sensitive substrate of the isoenzyme.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided. Otherwise, caution is advised if coadministration is required. Dosage adjustments may be necessary in patients who experience excessive adverse effects of tizanidine such as drowsiness, dizziness, lightheadedness, hypotension, and bradycardia.', 'Metabolism', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 'Carisoprodol, Orphenadrine, Chlorzoxazone, Baclofen, Methocarbamol, Chlormezanone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24969/', '[1] Granfors MT, Backman JT, Neuvonen M, Ahonen J, Neuvonen PJ "Fluvoxamine drastically increases concentrations and effects of tizanidine: A potentially hazardous interaction." Clin Pharmacol Ther 75 (2004): 331-41[2] "Product Information. Vioxx (rofecoxib)." Merck & Co, Inc, West Point, PA.[3] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[4] Granfors MT, Backman JT, Neuvonen M, Neuvonen PJ "Ciprofloxacin greatly increases concentrations and hypotensive effect of tizanidine by inhibiting its cytochrome P450 1A2-mediated presystemic metabolism." Clin Pharmacol Ther 76 (2004): 598-606[5] Momo K, Doki