    ) VALUES (1338786, 'Fosphenytoin', 'Apremilast', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of apremilast, which is primarily metabolized by the isoenzyme in vitro.', NULL, 'Concomitant use of apremilast with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of apremilast with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
