    ) VALUES (1338810, 'Fosphenytoin', 'Bosutinib', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of bosutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of bosutinib with potent and moderate CYP450 3A4 inducers should be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of bosutinib with potent and moderate CYP450 3A4 inducers should be avoided due to the potential for reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
