    ) VALUES (1338811, 'Human botulinum neurotoxin A/B immune globulin', 'Fosphenytoin', 'Moderate', 'A possible interaction between phenytoin and intravenous immunoglobulin (IVIG) was suspected in a case of fatal hypersensitivity myocarditis.', NULL, 'Due to the serious nature of the possible interaction, clinicians should consider monitoring hematologic parameters such as eosinophil counts as well as blood pressure and ECG tracings if immunoglobulins are used in patients receiving phenytoin.', NULL, 'Due to the serious nature of the possible interaction, clinicians should consider monitoring hematologic parameters such as eosinophil counts as well as blood pressure and ECG tracings if immunoglobulins are used in patients receiving phenytoin.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
