        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338833, 'Fosphenytoin', 'Cannabidiol', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 and/or CYP450 2C19 may decrease the plasma concentrations of cannabidiol. The proposed mechanism of action is induction of CYP450 3A4 and/or 2C19-mediated metabolism of cannabidiol.', NULL, 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', NULL, 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
