        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338835, 'Fosphenytoin', 'Capmatinib', 'Major', 'Coadministration with strong or moderate CYP450 3A4 inducers may decrease the plasma concentrations and anti-tumor activity of capmatinib. The proposed mechanism involves induction of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', NULL, 'Coadministration of capmatinib with strong or moderate CYP450 3A4 inducers should be avoided.', NULL, 'Coadministration of capmatinib with strong or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
