        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338836, 'Fosphenytoin', 'Carbamazepine', 'Moderate', 'Hydantoins may decrease carbamazepine levels, and carbamazepine may have variable effects on hydantoin levels. The mechanism may be related to induction of CYP450 hepatic metabolism of carbamazepine and alteration of hydantoin metabolism.', NULL, 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued.', NULL, 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or symptoms of hydantoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
