        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338838, 'Fosphenytoin', 'Carboplatin', 'Moderate', 'Carboplatin may decrease serum phenytoin levels. Loss of seizure control may result. The mechanism of this interaction is unknown. It is not know if carboplatin will interact in a similar manner with other hydantoins.', NULL, 'If carboplatin and phenytoin must be used together, close clinical and laboratory observation for evidence of altered phenytoin effect is recommended.', NULL, 'If carboplatin and phenytoin must be used together, close clinical and laboratory observation for evidence of altered phenytoin effect is recommended. Patients should be advised to notify their caregiver if they experience loss of seizure control.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
