        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338855, 'Fosphenytoin', 'Cisatracurium', 'Moderate', 'Case reports have suggested that long-term phenytoin administration may decrease the effectiveness of neuromuscular blocker and increase the dosage requirements. One of the proposed mechanisms is hepatic enzyme induction by phenytoin. Conversely, acute phenytoin administration may increase neuromuscular blocking effects by an unknown mechanism. Other hydantoins may interact with other non-depolarizing neuromuscular blockers in a similar fashion.', NULL, 'Close observation for evidence of altered effects of neuromuscular blocking agents is indicated if these drugs must be used together.', NULL, 'Close observation for evidence of altered effects of neuromuscular blocking agents is indicated if these drugs must be used together.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
