        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338889, 'Fosphenytoin', 'Dexmethylphenidate', 'Minor', 'According to the manufacturer, methylphenidate may inhibit the metabolism of anticonvulsants including phenobarbital, phenytoin, and primidone. There have been only isolated case reports of phenytoin intoxication during coadministration with methylphenidate and none for the other anticonvulsants. Nevertheless, it may be appropriate to monitor the pharmacologic response to these anticonvulsants more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the anticonvulsant dosage adjusted as necessary.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
