        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338892, 'Fosphenytoin', 'Diazoxide', 'Moderate', 'Phenytoin may significantly decrease the effects of diazoxide and that diazoxide may significantly decrease the effects of phenytoin. The proposed mechanism is induction of phenytoin metabolism and clearance by diazoxide. Phenytoin reduces the half-life of diazoxide. Loss of seizure control has occurred.', NULL, 'Caution and close observation are recommended if these drugs must be used together.', NULL, 'Caution and close observation are recommended if these drugs must be used together. Patients should be monitored for seizure activity and altered diazoxide effects (e.g., hypoglycemia).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
