        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338898, 'Fosphenytoin', 'Valproic acid', 'Moderate', 'Valproic acid (and its derivatives) may increase the pharmacologic effects of phenytoin. Toxicity may result, despite normal phenytoin levels. The mechanism may be related to displacement of phenytoin from plasma proteins. Also, phenytoin may induce the CYP450 metabolism of valproic acid. Similar interactions may occur with other hydantoins.', NULL, 'Patients on combination therapy should be closely monitored for clinical and laboratory evidence of altered phenytoin and valproate effects.', NULL, 'Patients on combination therapy should be closely monitored for clinical and laboratory evidence of altered phenytoin and valproate effects. It may be more useful to monitor plasma free-phenytoin levels than to monitor total plasma concentrations. Patients should be advised to notify their physician if they experience symptoms of toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia).', 'Metabolism, Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
