        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338900, 'Fosphenytoin', 'Dofetilide', 'Minor', 'Dofetilide is metabolized to a small extent by CYP450 3A4 microsomal enzymes in the liver. The clinician should be aware of the potential for an interaction when dofetilide is used in combination with another medication that induces CYP450 3A4.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
