        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338901, 'Fosphenytoin', 'Donepezil', 'Moderate', 'Coadministration with drugs that are inducers of the CYP450 3A4 and/or 2D6 enzymatic pathways may decrease the plasma concentrations of donepezil. The mechanism is accelerated clearance of donepezil due to enhanced CYP450 activities.', NULL, 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', NULL, 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
