        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338902, 'Fosphenytoin', 'Dopamine', 'Moderate', 'Coadministration of dopamine and intravenous hydantoin derivatives may result in sudden and profound hypotension and bradycardia. The mechanism may be a combination of catecholamine depletion and hydantoin-induced myocardial depression, and the effect may be dose-rate dependent. Fatalities have been reported, but causality is unknown. Data are available for intravenous phenytoin only.', NULL, 'Extreme caution and frequent blood pressure monitoring are recommended if concomitant use is necessary.', NULL, 'Extreme caution and frequent blood pressure monitoring are recommended if concomitant use is necessary. Alternative anticonvulsants may be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
