        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1338922, 'Fosphenytoin', 'Empagliflozin', 'Moderate', 'Coadministration of empagliflozin with inducers of uridine diphosphate glucuronosyl transferase (UGT) enzymes may decrease the plasma concentrations and efficacy of empagliflozin, which is primarily metabolized in vitro by UGT1A3, 1A8, 1A9, and 2B7.', NULL, 'Coadministration of empagliflozin with UGT inducers should be avoided if possible.', NULL, 'Coadministration of empagliflozin with UGT inducers should be avoided if possible. If concomitant use is required, dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever a UGT inducer is added to or withdrawn from empagliflozin therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
