    ) VALUES (1338926, 'Fosphenytoin', 'Prochlorperazine', 'Moderate', 'Concurrent use of phenothiazines and phenytoin may increase the effect of phenytoin and decrease the effect of the phenothiazine.', NULL, 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated.', NULL, 'Close monitoring for clinical and laboratory evidence of phenytoin toxicity is indicated. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
