INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50116, 'Budesonide', 'Mifepristone', 'Major', 'Mifepristone is a potent antiglucocorticoid and may antagonize the effects of corticosteroids. Pharmacokinetically, mifepristone may increase the plasma concentrations of corticosteroids by inhibiting their metabolism via CYP450 3A4. However, the clinical impact has not been studied. Dexamethasone, specifically, may also decrease the blood levels of mifepristone by inducing its metabolism via CYP450 3A4.', NULL, 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy.', NULL, 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy. When intended for daily use to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone is also contraindicated in those patients who require systemic corticosteroids for serious medical conditions or illnesses, such as immunosuppression after organ transplantation. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism, Antagonism', 'Ospemifene, Ulipristal, Prasterone, Bazedoxifene', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Cromoglicic acid, Ipratropium, Tiotropium, Mometasone furoate, Amcinonide, Fluocinolone acetonide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50187/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767369485);
