INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50192, 'Budesonide', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the systemic bioavailability of budesonide, which undergoes extensive first-pass and systemic metabolism via intestinal and hepatic CYP450 3A4. In pharmacokinetic studies, 6- to 8-fold increases in budesonide systemic exposure (AUC) have been observed during coadministration of the potent CYP450 3A4 inhibitor ketoconazole with different oral formulations of budesonide.', NULL, 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors.', NULL, 'The possibility of increased systemic adverse effects of budesonide should be considered during coadministration with CYP450 3A4 inhibitors. If concomitant use cannot be avoided, the dosing times between budesonide and the CYP450 3A4 inhibitor should be separated by as much as possible. In addition, the lowest effective dosage of budesonide should be prescribed, and further adjustments made as necessary according to therapeutic response and tolerance.', 'Metabolism', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Beclomethasone dipropionate, Terbutaline, Aclidinium, Glycopyrronium, Salbutamol, Orciprenaline, More', 'Tiagabine, Ethotoin, Brivaracetam, Cannabidiol, Methsuximide, Ezogabine, Paramethadione, Perampanel, Zonisamide, Fenfluramine, Phensuximide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50263/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Bolland MJ, Bagg W, Thomas MG, Lucas JA, Ticehurst R, Black PN "Cushing''s syndr