INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50401, 'Glycopyrronium', 'Pramlintide', 'Moderate', 'The use of pramlintide in combination with drugs that alter gastrointestinal motility (e.g., loperamide; anticholinergic agents) or drugs that slow the intestinal absorption of nutrients (e.g., alpha-glucosidase inhibitors) has not been studied.', NULL, 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', NULL, 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', 'Absorption', 'Umeclidinium, Budesonide, Aclidinium, Mometasone, Olodaterol, Fluticasone, Cromoglicic acid, Ipratropium, Pirbuterol, Salmeterol, Bitolterol, More', 'Glipizide, Acetohexamide, Guar gum, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50472/', '[1] "Product Information. Symlin (pramlintide)." Amphastar Pharmaceuticals Inc, South El Monte, CA.[2] "Product Information. Symlin (pramlintide)." Amphastar Pharmaceuticals Inc, South El Monte, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50402, 'Glycopyrronium', 'Procainamide', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.