INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50462, 'Carboplatin', 'Bumetanide', 'Moderate', 'The concomitant use of loop diuretics with carboplatin may increase the risk of ototoxicity. Loop diuretics, especially ethacrynic acid and high doses of furosemide, and platinum compounds have been individually associated with ototoxicity. Carboplatin is generally considered to be less neurotoxic and ototoxic than cisplatin; however, hearing loss has been reported when pediatric patients received high doses of carboplatin with other ototoxic agents.', NULL, 'It is recommended to regularly monitor auditory function if concomitant use is necessary.', NULL, 'It is recommended to regularly monitor auditory function if concomitant use is necessary. Patients should be advised to promptly notify their physician if they experience hearing loss or tinnitus.', 'Synergism', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Ciclopirox, Vemurafenib, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50533/', '[1] Langer T, am Zehnhoff-Dinnesen A, Radtke S, Meitert J, Zolk O "Understanding platinum-induced ototoxicity." Trends Pharmacol Sci 34 (2013): 458-69[2] "Product Information. Paraplatin (carboplatin)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Edecrin (ethacrynic acid)." Merck & Co, Inc, West Point, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Parsons SK, Neault MW, Lehm