INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50629, 'Bumetanide', 'Lomefloxacin', 'Minor', 'The plasma concentration of lomefloxacin following administration of furosemide has been reported to be higher than after lomefloxacin alone. The area under the curve of lomefloxacin was increased and the total and renal clearances of lomefloxacin were decreased. A similar interaction may occur if other quinolones are administered with other loop diuretics. The loop diuretic may decrease the renal clearance of the quinolone by competing for tubular secretion.', NULL, '-', NULL, '-', 'Excretion', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', 'Rifamycin, Polymyxin B, Sulfamethizole, Trifluridine, Benzylpenicillin, Ampicillin, Chloramphenicol, Sulfisoxazole, Azithromycin, Chlorhexidine, Levofloxacin, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50700/', '[1] Sudoh T, Fujimura A, Shiga T, Sasaki M, Harada K, Tateishi T, Ohashi K, Ebihara A "Renal clearance of lomefloxacin is decreased by furosemide." Eur J Clin Pharmacol 46 (1994): 267-9', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50630, 'Bumetanide', 'Loracarbef', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', 'Imipenem, Aztreonam, Meropenem, Cefiderocol, Ertapenem, Doripenem', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50701/', '[1] Norrby R, Stenqvist K, Elgefors B "Interaction between cephaloridine and furosemide in man." Scand J Infect Dis 8 (1976): 209-12[2] Chrysos G, Gargalianos P, Lelekis M, Stefanou J, Kosmidis J "Pharmacokinetic interactions of ceftazidime and frusemide." J Chemother 7 Suppl (1995): 107-10[3] Dodds MG, Foord RD "Enhancement by potent diuretics of renal tubular necrosis induced by cephaloridine." Br J Pharmacol 40 (1970): 227-36[4] Simpson IJ "Nephrotoxicity and acute renal failure associated with cephalothin and cephaloridine." N Z Med J 74 (1971): 312-5[5] Korn A, Eichler HG, Gasic S "A drug interaction study of ceftriaxone and frusemide in healthy volunteers." Int J Clin Pharmacol Ther Toxicol 24 (1986): 262-4[6] Trollfors B, Norrby R, Kristianson K, Nilsson NJ "Effects on renal function of treatment with cefoxitin alone or in combination with furosemide." Scand J Infect Dis 13 (1978): 73-7[7] Lawson DH, Macadam RF, Singh H, et al "Effect of furosemide on antibiotic-induced renal damage in rats." J Infect Dis 126 (1972): 593-600[8] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[9] "Product Information. Lorabid (loracarbef)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50631, 'Bumetanide', 'Lorazepam', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Halazepam', 'Tinidazole, Mitapivat, Ascorbic acid, Oxytetracycline, Sulfamethoxazole, Sulfamethizole, Simeprevir, Vemurafenib, Darunavir, Povidone-iodine, Sulfisoxazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50702/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side