INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50766, 'Bumetanide', 'Tiludronic acid', 'Moderate', 'Coadministration of bisphosphonates and loop diuretics may potentiate the risk of hypocalcemia. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. These effects may be additive with those of loop diuretics, which tend to increase the urinary excretion of calcium.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics. Renal function as well as serum calcium levels should be monitored during and after treatment, since the effects of bisphosphonates can continue for many weeks after discontinuation. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasm, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 'Romosozumab, Cholecalciferol, Denosumab, Burosumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/50837/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767369485);
