INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50967, 'Duvelisib', 'Buprenorphine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of buprenorphine, which is partially metabolized (approximately 30%) by the isoenzyme.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if buprenorphine is prescribed with CYP450 3A4 inhibitors. Induction with buprenorphine should begin at a reduced dosage, and dosage escalation should occur more slowly to allow for assessment of opiate effects and development of patient tolerance. In patients who are already stabilized on buprenorphine, pharmacologic response and vital signs should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Morphine, Oxymorphone, Tapentadol, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51038/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (50968, 'Echinacea', 'Buprenorphine', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Nalmefene, Nicotine, Codeine, Morphine, Naltrexone, Oxymorphone, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/51039/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
