INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339125, 'Furosemide', 'Alogliptin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', NULL, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339126, 'Furosemide', 'Alprazolam', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339127, 'Furosemide', 'Amifostine', 'Moderate', 'Concomitant use with hypotensive agents may increase the risk and severity of hypotension associated with amifostine. Hypotension has been reported frequently in patients treated with amifostine. It is usually transient (lasting from 5 to 15 minutes) and closely associated with amifostine administration, but may persist or become evident hours after administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration. Blood pressure should be closely monitored during and after treatment. In addition to hypotension, transient hypertension or exacerbation of preexisting hypertension may occur from intravenous hydration, discontinuation of antihypertensive medications, or other causes. Patients who continue their hypotensive medication(s) during amifostine therapy should be carefully monitored for the development of hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339128, 'Furosemide', 'Amikacin', 'Major', 'Coadministration of parenteral or inhaled aminoglycoside antibiotics or oral neomycin in combination with loop diuretics may potentiate the risk of oto- or nephrotoxicity due to additive or synergistic pharmacologic effects of these drugs and or altered aminoglycoside serum and tissue levels. Coadministration of aminoglycosides with intravenous mannitol may increase the risk of nephrotoxicity. The risk may be greater with high dosages of either drug, preexisting renal insufficiency, advanced age, dehydration, or the presence of other oto- or nephrotoxic drugs. The onset of ototoxicity may be greatly delayed, and cochlear damage may initially be asymptomatic.', NULL, 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided.', NULL, 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided. Renal function tests and serial, vestibular, and audiometric tests should be performed as appropriate for the type of diuretic before and during therapy if coadministration is necessary.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
