INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (25590, 'Voriconazole', 'Testosterone', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', NULL, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Methyltestosterone, Fluoxymesterone, Prasterone, Methyltestosterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/25609/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Informatio