INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26151, 'Sodium citrate', 'Axitinib', 'Minor', 'Since the aqueous solubility of axitinib is pH-dependent, with higher pH resulting in lower solubility, drugs that increase the pH of the upper gastrointestinal tract may reduce the oral bioavailability of axitinib. The interaction has been studied with rabeprazole. No dosage adjustment for axitinib is recommended during coadministration with proton pump inhibitors, H2-receptor antagonists, or antacids.', NULL, '-', NULL, '-', 'Absorption', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, More', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26183/', '[1] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26152, 'Sodium citrate', 'Bacampicillin', 'Moderate', 'Coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents with acid neutralizing or reducing effects may decrease the oral bioavailability and plasma concentrations of ampicillin from bacampicillin. The exact mechanism of interaction is unknown but may be related to degradation of the prodrug when gastric pH is increased.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. Preferably, these agents should be avoided during therapy with bacampicillin, or an alternative antibiotic be prescribed if these medications cannot be discontinued. Patients treated with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) may minimize the effects of the interaction with bacampicillin by separating the times of administration by at least 2 hours.', 'Absorption', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, Nafcillin, More', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26184/