INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26225, 'Sodium citrate', 'Perindopril', 'Minor', 'Coadministration with antacids may decrease the oral bioavailability of captopril and other angiotensin converting enzyme (ACE) inhibitors due to delayed gastric emptying and/or elevated gastric pH. Based on available data, the clinical significance of this interaction appears to be minor. As a precaution, patients may want to consider separating the administration times of ACE inhibitors and antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) by 1 to 2 hours.', NULL, '-', NULL, '-', 'Absorption', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Simvastatin, Atorvastatin, Lovastatin, More', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Chlorhexidine, Neomycin', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26257/', '[1] Mantyla R, Mannisto PT, Vuorela A, Sundberg S, Ottoila P "Impairment of captopril bioavailability by concomitant food and antacid intake." Int J Clin Pharmacol Ther Toxicol 22 (1984): 626-9[2] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[3] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[4] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26226, 'Sodium citrate', 'Pexidartinib', 'Moderate', 'INTERVAL: Coadministration with acid-reducing agents may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption. The effect of coadministration with an H2-receptor antagonist or locally-acting antacids on pexidartinib pharmacokinetics is unknown.', NULL, 'If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist.', NULL, 'If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist. Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', 'Absorption', 'Sorbitol, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26258/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
