INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26275, 'Brimonidine (ophthalmic)', 'Loperamide', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Although the interaction has not been specifically studied, the possibility of an additive or potentiating effect with central nervous system (CNS) depressants such as alcohol, barbiturates, opiates, anxiolytics, sedatives, and anesthetics should be considered.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness. Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia.', 'Synergism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Aminobenzoic acid, Ivermectin, Caffeine, Dupilumab, Tralokinumab, Brimonidine, More', 'Eluxadoline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26307/', '[1] "Product Information. Mirvaso (brimonidine topica