INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339294, 'Gadobenic acid', 'Docetaxel', 'Moderate', 'Gadobenate dimeglumine may compete with other substrates of the canalicular multispecific organic anion transporter, also known as cMOAT or MRP2, which is an endogenous transporter protein that works in coordination with the conjugation process to facilitate drug excretion. In solid tumor cells, overexpression of MRP2 confers resistance to a wide variety of anticancer chemotherapeutic agents. Substrates of MRP2 include anthracyclines, protease inhibitors, taxanes, vinca alkaloids, cisplatin, etoposide, methotrexate, and tamoxifen. Theoretically, competition for MRP2 transport may interfere with the clearance of these drugs and/or gadobenate dimeglumine.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2. Patients should be monitored for potentially increased pharmacologic effects of gadobenate dimeglumine as well as the coadministered drug(s).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
