INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52137, 'Butalbital', 'Thiethylperazine', 'Moderate', 'Concomitant use of phenothiazines with barbiturates may reduce the plasma concentrations and therapeutic effects of both agents. The mechanism is unclear but may relate to metabolic induction of CYP450 hepatic enzymes. In addition, coadministration of phenothiazines and barbiturates may lead to a reduction in seizure threshold and additive central nervous system (CNS) and respiratory depressant effects.', NULL, 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US).', NULL, 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US). If coadministration is required, caution as well as clinical and laboratory monitoring should be considered whenever either of these agents is added to or withdrawn from therapy. If chlorpromazine is coadministered with a barbiturate, the manufacturer of chlorpromazine recommends using 1/4 to 1/2 of the usual barbiturate dosage. Patients should be monitored for hypotension, loss of seizure control and the development of adverse effects including CNS and respiratory depression.', 'Others', 'Nitrazepam, Paraldehyde, Quazepam, Estazolam, Ethchlorvynol, Zolpidem, Zopiclone, Zaleplon, Dichloralphenazone', 'Ketotifen, Chlorcyclizine, Azelastine, Fexofenadine, Dexchlorpheniramine, Loratadine, Phenindamine, Astemizole, Desloratadine, Meclizine, Cyclizine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52208/', '[1] Ellenor GL, Musa MN, Beuthin FC "Phenobarbital--thiori