INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52202, 'Cimetidine', 'Butorphanol', 'Moderate', 'Cimetidine may increase the action of narcotic analgesics. Cases of respiratory depression and apnea have been reported. The mechanism is unknown.', NULL, 'Patients'' vital signs should be closely monitored while receiving this combination.', NULL, 'Patients'' vital signs should be closely monitored while receiving this combination. If clinically significant respiratory depression occurs, the drugs should be withdrawn. The use of a narcotic antagonist, such as naloxone, may be needed.', 'Others', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Vonoprazan, Amoxicillin', 'Naloxone, Naltrexone, Tapentadol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52273/', '[1] Lam AM "Potentially lethal interaction of cimetidine and morphine." Can Med Assoc J 125 (1981): 820[2] Fine A, Churchill DN "Potentially lethal interaction of cimetidine and morphine." Can Med Assoc J 124 (1981): 1434, 1436[3] Lam AM, Clement JL "Effect of cimetidine premedication on morphine-induced ventilatory depression." Can Anaesth Soc J 31 (1984): 36-43', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52203, 'Ciprofloxacin', 'Butorphanol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of butorphanol according to the product labeling. Increased butorphanol concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease butorphanol plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to butorphanol.', NULL, 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', NULL, 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath. When discontinuing CYP450 3A4 inhibitors, monitor patients closely at frequent intervals and consider increasing the opioid dosage if needed to maintain adequate analgesia or if symptoms of opioid withdrawal occur.', 'Metabolism', 'Tinidazole, Tetracycline, Cefepime, Norfloxacin, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, Ofloxacin, More', 'Codeine, Naltrexone, Oxymorphone, Tapentadol, Dezocine, Opium, Pentazocine, Dihydrocodeine, Naloxone, Nalbuphine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52274/', '[1] "Product Information. Butorphanol Tartrate (butorphanol)." Apotex Corporation, Weston, FL.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767369485);
