INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52289, 'Fospropofol', 'Butorphanol', 'Moderate', 'Additive central nervous system and cardiorespiratory depressant effects may occur when fospropofol or propofol is administered with other depressants such as sedative-hypnotic agents and narcotic analgesics.', NULL, 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly.', NULL, 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly. Supportive therapy should be provided if needed.', 'Synergism', 'Naltrexone, Tapentadol, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52360/', '[1] McClune S, McKay AC, Wright PM, et al "Synergistic interaction between midazolam and propofol." Br J Anaesth 69 (1992): 240-5[2] "Product Information. Lusedra (fospropofol)." Eisai Inc, Teaneck, NJ.[3] "Product Information. Diprivan (propofol)." Zeneca Pharmaceuticals, Wilmington, DE.[4] Pavlin DJ, Coda B, Shen DD, et al "Effects of combining propofol and alfentanil on ventilation, analgesia, sedation, and emesis in human volunteers." Anesthesiology 84 (1996): 23-37[5] Gill SS, Wright EM, Reilly CS "Pharmacokinetic interaction of propofol and fentanyl: single bolus injection study." Br J Anaesth 65 (1990): 760-5[6] Hamaoka N, Oda Y, Hase I, Mizutani K, Nakamoto T, Ishizaki T, Asada A "Propofol decreases the clearance of midazolam by inhibiting CYP3A4: An in vivo and in vitro study." Clin Pharmacol Ther 66 (1999): 110-7', 1767369485)