INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52408, 'Butorphanol', 'Opium', 'Major', 'Concomitant use of opioids with other central nervous system (CNS) depressants including mixed agonist-antagonist or partial agonist opioids may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased.', NULL, 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary (e.g., when initiating a switch from one opioid to the other), the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, and patients should be closely monitored for signs and symptoms of CNS and respiratory depression. Additional caution is advisable when a mixed agonist-antagonist or partial agonist opioid is added to an existing opioid regimen, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the mixed agonist-antagonist or partial agonist opioid.', 'Synergism', 'Acetaminophen, Opium, Ibuprofen, Celecoxib, Acetylsalicylic acid', 'Acetylsalicylic acid, Butorphanol', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/52479/', '[1] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, 