INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52957, 'Conivaptan', 'Cabozantinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme. High plasma levels of cabozantinib may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of cabozantinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of cabozantinib with potent CYP450 3A4 inhibitors should generally be avoided. If concomitant use is required, the daily dosage of cabozantinib should be reduced by 40 mg (e.g., 140 mg to 100 mg daily; 100 mg to 60 mg daily). The original dosage may be resumed 2 to 3 days after discontinuation of the potent CYP450 3A4 inhibitor. However, some authorities recommend avoiding concomitant use of cabozantinib during and for 2 weeks after treatment with itraconazole. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be advised regarding a potential increase in more common side effects of cabozantinib, such as diarrhea, fatigue, nausea, decreased appetite, palmar-plantar erythrodysesthesia syndrome (PPES), hypertension, vomiting, weight loss, and constipation.', 'Metabolism', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53028/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52958, 'Crizotinib', 'Cabozantinib', 'Major', 'Crizotinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting crizotinib therapy and periodically during treatment.', 'Synergism', 'Dacomitinib, Tepotinib, Selumetinib, Capmatinib, Tivozanib, Axitinib, Alpelisib, Erdafitinib, Neratinib, Cabozantinib, Infigratinib, More', 'Regorafenib, Alpelisib, Trametinib, Erlotinib, Erdafitinib, Crizotinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53029/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52959, 'Cyclosporine', 'Cabozantinib', 'Moderate', 'Coadministration of cabozantinib with cyclosporine may increase exposure and toxicity of cabozantinib and/or cyclosporine. Both interactions are based on in vitro data. The proposed mechanisms include inhibition of P-glycoprotein-mediated drug efflux by cabozantinib (a P-gp inhibitor) which may potentially decrease the clearance and increase plasma concentrations of cyclosporine (a P-gp substrate). Additionally, cyclosporine, a multidrug resistance-associated protein inhibitor (MRP2 inhibitor) may increase cabozantinib (an MRP2 substrate) plasma concentrations due to inhibition of cabozantinib metabolism.', NULL, 'Caution is advised if cabozantinib is used concomitantly with cyclosporine.', NULL, 'Caution is advised if cabozantinib is used concomitantly with cyclosporine. Monitor for side effects of cabozantinib, such as diarrhea, hypertension, palmar-plantar erythrodysesthesia, hemorrhage, thrombotic events, or gastrointestinal perforation. For patients stabilized on a cyclosporine regimen, monitor blood levels and renal function. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever cabozantinib or cyclosporine are added to or withdrawn from combined therapy.', 'Absorption', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Sunitinib, Regorafenib, Midostaurin, Copanlisib, Lenvatinib, Erlotinib, Abemaciclib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddi