INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52996, 'Duvelisib', 'Cabozantinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', NULL, 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if cabozantinib is used with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as nausea, vomiting, diarrhea, anorexia, weight loss, stomatitis, gastrointestinal perforations and fistulas, palmar-plantar erythrodysesthesia syndrome, hemorrhage, wound complications, hypertension, neutropenia, and thrombocytopenia.', 'Metabolism', 'Futibatinib, Copanlisib, Alpelisib, Dacomitinib, Lenvatinib, Erdafitinib, Trametinib, Afatinib, Alectinib, Duvelisib, Tivozanib, More', 'Dacomitinib, Tepotinib, Selumetinib, Capmatinib, Tivozanib, Axitinib, Alpelisib, Erdafitinib, Neratinib, Cabozantinib, Infigratinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53067/', '[1] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.[2] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (52997, 'Echinacea', 'Cabozantinib', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'Entrectinib, Imatinib, Pralsetinib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53068/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
